#define ASCII_p          0x70 // p
#define ASCII_q          0x71 // q
#define ASCII_r          0x72 // r
#define ASCII_s          0x73 // s
#define ASCII_t          0x74 // t
#define ASCII_u          0x75 // u
#define ASCII_v          0x76 // v
#define ASCII_w          0x77 // w
#define ASCII_x          0x78 // x
